import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { storage, auth, provider } from "./firebase";
import { signInWithPopup, onAuthStateChanged, signOut } from "firebase/auth";
import { ref, uploadBytes, getDownloadURL, listAll } from "firebase/storage";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [posts, setPosts] = useState([]);
  const [currentPostIndex, setCurrentPostIndex] = useState(0);
  const [caption, setCaption] = useState("");
  const [videoUpload, setVideoUpload] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setIsAdmin(user.email === "epsilonclasher12@gmail.com");
      } else {
        setIsAuthenticated(false);
        setIsAdmin(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const videoRef = ref(storage, "images/");
        const metadataRef = ref(storage, "metadata/");
        const videoSnapshot = await listAll(videoRef);
        const metadataSnapshot = await listAll(metadataRef);

        const videoUrls = await Promise.all(videoSnapshot.items.map(item => getDownloadURL(item)));
        const metadataUrls = await Promise.all(metadataSnapshot.items.map(item => getDownloadURL(item)));

        const fetchedPosts = await Promise.all(metadataUrls.map(async (url, index) => {
          const response = await fetch(url);
          const metadata = await response.json();
          return {
            ...metadata,
            videoUrl: videoUrls[index]
          };
        }));

        setPosts(fetchedPosts);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []);

  const login = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Error logging in:", error);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const uploadVideo = async () => {
    if (videoUpload == null || caption === "") return;

    try {
      const videoRef = ref(storage, `images/${videoUpload.name}`);
      await uploadBytes(videoRef, videoUpload);
      const videoUrl = await getDownloadURL(videoRef);

      const metadata = {
        caption,
        timestamp: new Date().toISOString(),
        videoUrl
      };

      const metadataBlob = new Blob([JSON.stringify(metadata)], { type: "application/json" });
      const metadataRef = ref(storage, `metadata/${videoUpload.name}.json`);
      await uploadBytes(metadataRef, metadataBlob);

      alert("Upload successful");
      setVideoUpload(null);
      setCaption("");
    } catch (error) {
      console.error("Error uploading video:", error);
    }
  };

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = posts[currentPostIndex].videoUrl;
    link.download = posts[currentPostIndex].videoUrl.split('/').pop();
    link.click();
  };

  return (
    <div className="App">
      {!isAuthenticated ? (
        <button onClick={login} className="btn btn-primary">Login with Google</button>
      ) : (
        <>
          <button onClick={logout} className="btn btn-secondary">Logout</button>
          {isAdmin && (
            <div className="upload-section">
              <input type="file" onChange={event => setVideoUpload(event.target.files[0])} />
              <input type="text" placeholder="Caption" value={caption} onChange={event => setCaption(event.target.value)} />
              <button onClick={uploadVideo} className="btn btn-success">Upload</button>
            </div>
          )}
          {posts.length > 0 && (
            <div className="video-section">
              <div className="video-container">
                <p className="caption">{posts[currentPostIndex].caption}</p>
                <video
                  src={posts[currentPostIndex].videoUrl}
                  controls
                  className="video-player"
                />
                <p className="timestamp">{new Date(posts[currentPostIndex].timestamp).toLocaleString()}</p>
              </div>
              <div className="controls">
                <button
                  onClick={() => setCurrentPostIndex((currentPostIndex - 1 + posts.length) % posts.length)}
                  className="btn btn-info"
                >
                  Previous
                </button>
                <button onClick={handleDownload} className="btn btn-warning">Download</button>
                <button
                  onClick={() => setCurrentPostIndex((currentPostIndex + 1) % posts.length)}
                  className="btn btn-info"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
