import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { storage, auth, provider, ref, uploadBytes, getDownloadURL } from "./firebase";
import { signInWithPopup, onAuthStateChanged, signOut } from "firebase/auth";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [posts, setPosts] = useState([]);
  const [currentPostIndex, setCurrentPostIndex] = useState(0);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const postsRef = ref(storage, 'posts/metadata.json');
        const url = await getDownloadURL(postsRef);
        const response = await fetch(url);
        const postData = await response.json();
        setPosts(postData);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setIsAdmin(user.email === "epsilonclasher12@gmail.com");
      } else {
        setIsAuthenticated(false);
        setIsAdmin(false);
      }
    });

    return () => unsubscribe();
  }, []);

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Error logging in:", error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setIsAuthenticated(false);
      setIsAdmin(false);
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const handleNextPost = () => {
    setCurrentPostIndex((prevIndex) => (prevIndex + 1) % posts.length);
  };

  const handlePreviousPost = () => {
    setCurrentPostIndex((prevIndex) =>
      prevIndex === 0 ? posts.length - 1 : prevIndex - 1
    );
  };

  const handleDownloadVideo = async () => {
    try {
      const url = posts[currentPostIndex].url;
      const response = await fetch(url);
      const blob = await response.blob();
      const link = document.createElement("a");
      link.href = window.URL.createObjectURL(blob);
      link.download = `video${currentPostIndex + 1}.mp4`;
      link.click();
    } catch (error) {
      console.error("Error downloading video:", error);
    }
  };

  const handleUploadVideo = async (event) => {
    event.preventDefault();
    const file = event.target.videoFile.files[0];
    const caption = event.target.caption.value;
    const videoRef = ref(storage, `videos/${file.name}`);
    const metadataRef = ref(storage, 'posts/metadata.json');

    try {
      await uploadBytes(videoRef, file);
      const url = await getDownloadURL(videoRef);

      const newPost = {
        caption: caption,
        url: url,
        timestamp: new Date().toISOString(),
      };

      const existingPostsResponse = await fetch(await getDownloadURL(metadataRef));
      const existingPosts = await existingPostsResponse.json();

      const updatedPosts = [...existingPosts, newPost];
      const blob = new Blob([JSON.stringify(updatedPosts)], { type: 'application/json' });

      await uploadBytes(metadataRef, blob);

      alert("Upload successful");
    } catch (error) {
      console.error("Error uploading video:", error);
      alert("Upload failed");
    }
  };

  return (
    <div className="App container-fluid">
      {!isAuthenticated ? (
        <div className="login-container">
          <h2>Login</h2>
          <button className="btn btn-primary" onClick={handleGoogleLogin}>
            Login with Google
          </button>
        </div>
      ) : (
        <>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Welcome {isAdmin ? "Admin" : "User"}</h2>
            <button className="btn btn-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
          <div className="content">
            <div className="video-container mb-4">
              {posts.length > 0 ? (
                <>
                  <div className="video-wrapper">
                    <video
                      key={currentPostIndex}
                      src={posts[currentPostIndex].url}
                      controls
                      className="mb-2"
                      style={{
                        maxWidth: "100%",
                        maxHeight: "75vh", // Adjust max height based on screen height
                      }}
                    />
                    <div className="caption">{posts[currentPostIndex].caption}</div>
                    <div className="timestamp">
                      {new Date(posts[currentPostIndex].timestamp).toLocaleString()}
                    </div>
                  </div>
                  <div className="d-flex justify-content-between mt-2">
                    <button className="btn btn-primary" onClick={handlePreviousPost}>
                      Previous
                    </button>
                    <button className="btn btn-secondary" onClick={handleNextPost}>
                      Next
                    </button>
                    <button className="btn btn-success" onClick={handleDownloadVideo}>
                      Download
                    </button>
                  </div>
                </>
              ) : (
                <p>No videos available</p>
              )}
            </div>
            {isAdmin && (
              <div className="upload-form mb-4">
                <h3>Upload Video</h3>
                <form onSubmit={handleUploadVideo}>
                  <div className="mb-3">
                    <label htmlFor="videoFile" className="form-label">Choose Video File</label>
                    <input type="file" className="form-control" id="videoFile" name="videoFile" />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="caption" className="form-label">Caption</label>
                    <input type="text" className="form-control" id="caption" name="caption" />
                  </div>
                  <button type="submit" className="btn btn-primary">Upload</button>
                </form>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
