import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { storage, auth, provider } from "./firebase";
import { signInWithPopup, onAuthStateChanged, signOut } from "firebase/auth";
import { ref, uploadBytes, getDownloadURL, listAll } from "firebase/storage";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setIsAdmin(user.email === "epsilonclasher12@gmail.com");
      } else {
        setIsAuthenticated(false);
        setIsAdmin(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const videoRef = ref(storage, "images/");
        const videoSnapshot = await listAll(videoRef);
        const videoUrls = await Promise.all(
          videoSnapshot.items.map((item) => getDownloadURL(item))
        );
        setVideos(videoUrls);
      } catch (error) {
        console.error("Error fetching videos:", error);
      }
    };

    fetchVideos();
  }, []);

  const login = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Error logging in:", error);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = videos[currentVideoIndex];
    link.download = videos[currentVideoIndex].split('/').pop();
    link.click();
  };

  return (
    <div className="App">
      {!isAuthenticated ? (
        <button onClick={login} className="btn btn-primary">Login with Google</button>
      ) : (
        <>
          <button onClick={logout} className="btn btn-secondary">Logout</button>
          {videos.length > 0 && (
            <div className="video-section">
              <div className="video-container">
                <video
                  src={videos[currentVideoIndex]}
                  controls
                  className="video-player"
                />
              </div>
              <div className="controls">
                <button
                  onClick={() => setCurrentVideoIndex((currentVideoIndex - 1 + videos.length) % videos.length)}
                  className="btn btn-info"
                >
                  Previous
                </button>
                <button onClick={handleDownload} className="btn btn-warning">Download</button>
                <button
                  onClick={() => setCurrentVideoIndex((currentVideoIndex + 1) % videos.length)}
                  className="btn btn-info"
                >
                  Next
                </button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
