import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { storage } from "./firebase";
import { ref, uploadBytes, listAll, getDownloadURL } from "firebase/storage";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [videoUpload, setVideoUpload] = useState(null);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const [isAdmin, setIsAdmin] = useState(true); // Change to false to simulate non-admin user

  useEffect(() => {
    const videoRef = ref(storage, "images");
    const fetchVideos = async () => {
      try {
        const response = await listAll(videoRef);
        const urls = await Promise.all(
          response.items.map(item => getDownloadURL(item))
        );
        setVideos(urls);
      } catch (error) {
        console.error("Error fetching videos:", error);
      }
    };
    fetchVideos();
  }, []);

  const uploadVideo = async () => {
    if (videoUpload == null) return;
    const videoRef = ref(storage, `images/${videoUpload.name}`);
    try {
      await uploadBytes(videoRef, videoUpload);
      alert("Upload successful");
      const url = await getDownloadURL(videoRef);
      setVideos((prev) => [...prev, url]);
    } catch (error) {
      console.error("Error uploading video:", error);
      alert("Upload failed");
    }
  };

  const handleNextVideo = () => {
    setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % videos.length);
  };

  return (
    <Router>
      <div className="App container">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link className="navbar-brand" to="/">Video App</Link>
          <div className="collapse navbar-collapse">
            <ul className="navbar-nav mr-auto">
              {isAdmin && (
                <li className="nav-item">
                  <Link className="nav-link" to="/upload">Upload Video</Link>
                </li>
              )}
              <li className="nav-item">
                <Link className="nav-link" to="/">Watch Videos</Link>
              </li>
            </ul>
          </div>
        </nav>

        <div className="content my-4">
          <Routes>
            <Route path="/" element={
              <div className="video-container">
                {videos.length > 0 ? (
                  <>
                    <video
                      key={currentVideoIndex}
                      src={videos[currentVideoIndex]}
                      controls
                      className="mb-2"
                      width="500"
                    />
                    <button className="btn btn-secondary" onClick={handleNextVideo}>
                      Next
                    </button>
                  </>
                ) : (
                  <p>No videos available</p>
                )}
              </div>
            } />
            {isAdmin && (
              <Route path="/upload" element={
                <div className="upload-container">
                  <input
                    type="file"
                    className="form-control mb-2"
                    onChange={(event) => setVideoUpload(event.target.files[0])}
                  />
                  <button className="btn btn-primary" onClick={uploadVideo}>
                    Upload
                  </button>
                </div>
              } />
            )}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
