import React, { useState, useEffect } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { storage, auth, provider } from "./firebase";
import { ref, listAll, getDownloadURL } from "firebase/storage";
import { signInWithPopup, onAuthStateChanged, signOut } from "firebase/auth";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

  useEffect(() => {
    const videoRef = ref(storage, "images");
    const fetchVideos = async () => {
      try {
        const response = await listAll(videoRef);
        const urls = await Promise.all(
          response.items.map(item => getDownloadURL(item))
        );
        setVideos(urls);
      } catch (error) {
        console.error("Error fetching videos:", error);
      }
    };
    fetchVideos();
  }, []);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setIsAuthenticated(true);
        setIsAdmin(user.email === "epsilonclasher12@gmail.com");
      } else {
        setIsAuthenticated(false);
        setIsAdmin(false);
      }
    });

    // Cleanup subscription on unmount
    return () => unsubscribe();
  }, []);

  const handleGoogleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error("Error logging in:", error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setIsAuthenticated(false);
      setIsAdmin(false);
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  const handleNextVideo = () => {
    setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % videos.length);
  };

  const handlePreviousVideo = () => {
    setCurrentVideoIndex((prevIndex) =>
      prevIndex === 0 ? videos.length - 1 : prevIndex - 1
    );
  };

  const handleDownloadVideo = async () => {
    try {
      const url = videos[currentVideoIndex];
      const response = await fetch(url);
      const blob = await response.blob();
      const link = document.createElement("a");
      link.href = window.URL.createObjectURL(blob);
      link.download = `video${currentVideoIndex + 1}.mp4`;
      link.click();
    } catch (error) {
      console.error("Error downloading video:", error);
    }
  };

  return (
    <div className="App container-fluid">
      {!isAuthenticated ? (
        <div className="login-container">
          <h2>Login</h2>
          <button className="btn btn-primary" onClick={handleGoogleLogin}>
            Login with Google
          </button>
        </div>
      ) : (
        <>
          <div className="d-flex justify-content-between align-items-center mb-4">
            <h2>Welcome {isAdmin ? "Admin" : "User"}</h2>
            <button className="btn btn-danger" onClick={handleLogout}>
              Logout
            </button>
          </div>
          <div className="content">
            <div className="video-container mb-4">
              {videos.length > 0 ? (
                <>
                  <video
                    key={currentVideoIndex}
                    src={videos[currentVideoIndex]}
                    controls
                    className="mb-2"
                    style={{
                      maxWidth: "100%",
                      height: "auto",
                      maxHeight: "75vh", // Adjust max height based on screen height
                    }}
                  />
                  <div className="d-flex justify-content-between">
                    <button className="btn btn-primary" onClick={handlePreviousVideo}>
                      Previous
                    </button>
                    <button className="btn btn-secondary" onClick={handleNextVideo}>
                      Next
                    </button>
                    <button className="btn btn-success" onClick={handleDownloadVideo}>
                      Download
                    </button>
                  </div>
                </>
              ) : (
                <p>No videos available</p>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
